const campaigns = $input.first().json;
const alerts = [];

// Check Meta campaigns
if (campaigns.meta) {
  for (const campaign of campaigns.meta) {
    const percentUsed = (campaign.spend / campaign.budget) * 100;

    if (percentUsed >= 100) {
      alerts.push({
        campaignId: campaign.id,
        campaignName: campaign.name,
        platform: 'Meta Ads',
        budget: campaign.budget,
        spend: campaign.spend,
        percentUsed: percentUsed.toFixed(1),
        alertLevel: 'CRITICAL',
        status: campaign.status,
        roas: campaign.roas,
        message: `âš ï¸ BUDGET EXHAUSTED: ${campaign.name}`
      });
    } else if (percentUsed >= 80) {
      alerts.push({
        campaignId: campaign.id,
        campaignName: campaign.name,
        platform: 'Meta Ads',
        budget: campaign.budget,
        spend: campaign.spend,
        percentUsed: percentUsed.toFixed(1),
        alertLevel: 'WARNING',
        status: campaign.status,
        roas: campaign.roas,
        message: `âš¡ HIGH SPEND: ${campaign.name} at ${percentUsed.toFixed(0)}%`
      });
    }
  }
}

// Check Google campaigns
if (campaigns.google) {
  for (const campaign of campaigns.google) {
    const percentUsed = (campaign.spend / campaign.budget) * 100;

    if (percentUsed >= 100) {
      alerts.push({
        campaignId: campaign.id,
        campaignName: campaign.name,
        platform: 'Google Ads',
        budget: campaign.budget,
        spend: campaign.spend,
        percentUsed: percentUsed.toFixed(1),
        alertLevel: 'CRITICAL',
        status: campaign.status,
        roas: campaign.roas,
        message: `âš ï¸ BUDGET EXHAUSTED: ${campaign.name}`
      });
    } else if (percentUsed >= 80) {
      alerts.push({
        campaignId: campaign.id,
        campaignName: campaign.name,
        platform: 'Google Ads',
        budget: campaign.budget,
        spend: campaign.spend,
        percentUsed: percentUsed.toFixed(1),
        alertLevel: 'WARNING',
        status: campaign.status,
        roas: campaign.roas,
        message: `âš¡ HIGH SPEND: ${campaign.name} at ${percentUsed.toFixed(0)}%`
      });
    }
  }
}

return alerts.map(alert => ({ json: alert }));

